var OF_Notification = function(message, mode) {

	var classes = 'of-system-message-mini ' + (typeof mode !== 'undefined' ? mode : 'success');
    
    var element = $('<div/>').addClass(classes),
        article = $('<div/>');

    article.html(message);
    article.appendTo(element);

    element.show().css("display", "block");
    
    this.element = element;

    $('.of-system-message-mini').remove();

    $('body').prepend(element);

    this.destruct = function() {

        var self = this;

        setTimeout(function() {
            self.element.remove();
        }, 3500);
    };

    this.destruct();

    

};